    d_str += d.getMinutes().toString().length == 1 ? '0' + d.getMinutes() : '' + d.getMinutes();
    d_str += d.getSeconds().toString().length == 1 ? '0' + d.getSeconds() : '' + d.getSeconds();
    return d_str;
